package task_18th_June_for;

public class Sum_first_10_natural_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum =0;
		for(int i =1; i<=10; i++) {
			sum+=i;
		}
	    System.out.println(sum + " is the sum of the first 10 natural numbers");

	}

}
