package task_18th_june_while;

public class Sum_first_10_natural_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n = 1;
		int sum = 0;
		while(n<=10) {
			sum+=n;
			n++;
		}
		System.out.println(sum + " is the sum of first 10 natural numbers");

	}

}
